// SPDX-License-Identifier: MIT

pragma solidity >=0.4.22 <0.9.0;

import "./BUVToken.sol";
import "./BUVNFT.sol";

contract NFTMarket {

    BUVToken _buvToken;
    BUVNFT _buvNft;

    mapping(uint256 => uint256) public _basePrice;

    constructor(address payable buvNft_, address payable buvToken_, uint256 tokenId_, uint256 price_) {
        _buvToken = BUVToken(buvToken_);
        _buvNft = BUVNFT(buvNft_);
        _basePrice[tokenId_] = price_; 
    }


}
